select.if:
SELECT  from module WHERE module_id
in(
	SELECT module_id from role_module where role_id in(
		select role_id from role where role_id in(
			select role_id from user_role where user_id = #{userId} 
		) and del_flag = 0
	)
) and del_flag = 0  
ORDER BY level ASC,orders ASC

mapper.select:
select m.module_id as moduleId,m.module_name as moduleName,m.page_url as pageUrl,
m.api_url as apiUrl,m.module_type as moduleType,m.alias,m.create_date as createDate
from module m

	m.del_flag = 0
	and m.module_name like CONCAT('%',#{moduleName},'%')
	and m.page_url like CONCAT('%',#{pageUrl},'%')
	and m.page_url like CONCAT('%',#{apiUrl},'%')
	and m.module_type = ${moduleType}
	and m.alias like CONCAT('%',#{alias},'%')
	and m.create_date >=#{createStartDate}
	and m.create_date <=#{createEndDate}

order by m.level asc,m.orders asc
limit ${(currentPage-1)*pageSize},${pageSize}

select count(1) from module m

	m.del_flag = 0
	and m.module_name like CONCAT('%',#{moduleName},'%')
	and m.page_url like CONCAT('%',#{pageUrl},'%')
	and m.page_url like CONCAT('%',#{apiUrl},'%')
	and m.module_type = ${moduleType}
	and m.alias like CONCAT('%',#{alias},'%')
	and m.create_date >=#{createStartDate}
	and m.create_date <=#{createEndDate}
