mapper.select:
select r.role_id as roleId,r.role_name as roleName,r.create_date as createDate
from role r

	r.del_flag = 0
	and r.role_name like CONCAT('%',#{roleName},'%')
	and r.create_date >=#{createStartDate}
	and r.create_date <=#{createEndDate}

order by r.create_date desc
limit ${(currentPage-1)*pageSize},${pageSize}

select count(1) from role r

	r.del_flag = 0
	and r.role_name like CONCAT('%',#{roleName},'%')
	and r.create_date >=#{createStartDate}
	and r.create_date <=#{createEndDate}

mapper.delete:

delete from role_module where role_id = #{_parameter};

delete from user_role where role_id = #{_parameter};

delete from role where role_id = #{_parameter};
