select.if:
SELECT module_id as moduleId,module_name as moduleName,page_url as pageUrl,api_url as apiUrl,module_type as moduleType,parent_id as parentId,icon from module WHERE module_id
in(
	SELECT rm.module_id from role_module rm
	WHERE rm.role_id in(
		SELECT ur.role_id from role r,user_role ur
		WHERE ur.user_id = #{userId} 
		AND r.role_id = ur.role_id
	)
) and del_flag = 0  
ORDER BY level ASC,orders ASC

mapper.select:
		SELECT SQL_CALC_FOUND_ROWS user_id as userId,user_name as userName,person_name as personName,create_date as createDate,
		(
			select GROUP_CONCAT(r.role_name) from role r where r.role_id in
			(select ur.role_id from user_role ur where ur.user_id = u.user_id) GROUP BY r.role_name
		) as roleName
		from user u

			u.del_flag = 0 
			and u.level > (SELECT IFNULL((select u2.level from user u2 where u2.user_id = #{userId}),1))		

				and u.user_name like CONCAT('%',#{userName},'%')


				and u.create_date >= #{startDate}


				and u.create_date <= #{endDate}


		limit ${currentStart},${currentSize};
      	SELECT FOUND_ROWS() as total;
