select.if:
SELECT moduleid,modulename,pageurl,apiurl,moduletype,parentid,icon from module WHERE moduleid
in(
	SELECT rm.moduleid from role_module rm
	WHERE rm.roleid in(
		SELECT ur.roleid from role r,user_role ur
		WHERE ur.userid = #{userId} 
		AND r.roleid = ur.roleid
	)
) and del_flag = 0  
ORDER BY level ASC,orders ASC
