mapper.insert:
delete from interfaces where id in

	#{item}

;
delete from user_data where data_permission_id in(
	select id from data_permission where interfaces_id not in(SELECT id from interfaces)
);
delete from role_data where data_permission_id in(
	select id from data_permission where interfaces_id not in(SELECT id from interfaces)
);
delete from data_permission where interfaces_id not in(SELECT id from interfaces);

mapper.select:
select id,name,url,method,times,unit,counts,data_permission as dataPermission,remark,create_date as createDate,creator,update_date as updateDate,updater,del_flag as delFlag from interfaces
del_flag = 0 and name like CONCAT('%',#{name},'%') and url like CONCAT('%',#{url},'%') and method like CONCAT('%',#{method},'%')
ORDER BY name desc limit ${(currentPage-1)*pageSize},${pageSize}

mapper.delete:
delete from data_permission;
delete from user_data;
delete from role_data;
