select.if:
SELECT module_id as moduleId,module_name as moduleName,page_url as pageUrl,api_url as apiUrl,parent_id as parentId, (select n.module_name from module n where n.module_id = module.parent_id and n.del_flag = 0) as parentName, fcode,level,orders,module_type as moduleType,alias,parent_alias as parentAlias,remark,icon, create_date as createDate,creator,update_date as updateDate,updater,del_flag as delFlag from module WHERE module_id in
#{id}
and del_flag = 0 ORDER BY level ASC,orders ASC

mapper.select:
select m.module_id as moduleId,m.parent_id as parentId,m.module_name as moduleName, (select n.module_name from module n where n.module_id = m.parent_id and n.del_flag = 0) as parentName, m.page_url as pageUrl,m.api_url as apiUrl,m.module_type as moduleType,m.alias,m.create_date as createDate from module m
m.del_flag = 0 and m.module_name like CONCAT('%',#{moduleName},'%') and m.page_url like CONCAT('%',#{pageUrl},'%') and m.page_url like CONCAT('%',#{apiUrl},'%') and m.module_type = ${moduleType} and m.alias like CONCAT('%',#{alias},'%') and m.create_date >=#{createStartDate} and m.create_date <=#{createEndDate}
order by m.level asc,m.orders asc limit ${(currentPage-1)*pageSize},${pageSize}

select count(1) from module m

	m.del_flag = 0
	and m.module_name like CONCAT('%',#{moduleName},'%')
	and m.page_url like CONCAT('%',#{pageUrl},'%')
	and m.page_url like CONCAT('%',#{apiUrl},'%')
	and m.module_type = ${moduleType}
	and m.alias like CONCAT('%',#{alias},'%')
	and m.create_date >=#{createStartDate}
	and m.create_date <=#{createEndDate}

mapper.delete:

delete from user_module where module_id = #{_parameter};

delete from role_module where module_id = #{_parameter};

delete from module where module_id = #{_parameter};
