mapper.select:
select ifnull((select us.user_name from user us where us.user_id = ol.user_id),'admin123456') as userName, ol.url,request_ip_address as requestIpAddress,request_time as requestTime from operation_log ol where 1=1
and ol.user_id in(select u.user_id from user u where u.user_name like CONCAT('%',#{userName},'%'))
and ol.request_ip_address like CONCAT('%',#{requestIpAddress},'%') order by ol.request_time desc limit ${(currentPage-1)*pageSize},${pageSize}
